{
	"callback_backoff_delay_ms": 3000,
  	"max_callback_backoff_delay_ms": 900000,
  	"callback_persist": true,
  	"media": {
  		"auto_download": ["image", "document", "audio"]
  	},
  	"webhooks": {
  		"url": "<Webhook URL, https>",
  		"max_concurrent_requests": 12
  	},
  	"on_call_pager": "<WA_ID of valid WhatsApp contact>",
  	"pass_through": false,
  	"sent_status": false
}

{
	"password": "<Password for Backup>",
	"data": "<Data to Restore, from Backup API>"
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "image",
	"recipient_type": "individual",
	"image": {
		"provider": {
			"name": "<Provider Name from Media Provider API, optional"
		},
		"link": "<Link to Image, https>",
		"caption": "<Media Caption>"
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "audio",
	"recipient_type": "individual",
	"audio": {
		"provider": {
			"name": "<Provider Name from Media Provider API, optional"
		},
		"link": "<Link to Audio, https>"
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "video",
	"recipient_type": "individual",
	"video": {
		"provider": {
			"name": "<Provider Name from Media Provider API, optional"
		},
		"caption": "<Message Caption>",
		"link": "<Link to Video, https>"
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "document",
	"recipient_type": "individual",
	"document": {
		"provider": {
			"name": "<Provider Name from Media Provider API, optional"
		},
		"caption": "<Message Caption>",
		"link": "<Link to PDF, https>",
		"filename": "<Filename>"
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "hsm",
	"hsm": {
		"namespace": "<Message Template Namespace>",
    	"element_name": "<Message Template Element Name>",
    	"language": {
    		"policy": "<Message Template Language Policy>",
    		"code": "<Message Template Language Code>"
    	},
	    "localizable_params": [{"default":"<param value>"}]
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"ttl": "<Message Template TTL>",
	"type": "hsm",
	"hsm": {
		"namespace": "<Message Template Namespace>",
    	"element_name": "<Message Template Element Name>",
    	"language": {
    		"policy": "<Message Template Language Policy>",
    		"code": "<Message Template Language Code>"
    	},
	    "localizable_params": [{"default":"<param value>"}]
	}
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "template",
	"template": {
		"namespace": "<Message Template Namespace>",
    	"name": "<Message Template Element Name>",
    	"language": {
    		"policy": "<Message Template Language Policy>",
    		"code": "<Message Template Language Code>"
    	},
    	"components": [
    	{
    		"type": "header",
    		"parameters": [
    		{
    			"type": "image",
				"caption": "<Message Caption>",
				"link": "<Media HTTPS URL>"
    		}
    		]
    	},
    	{
    		"type": "body",
    		"parameters": [
    		{
    			"type": "text",
    			"text": "<Message Text Param>"
    		}
    		]
    	}
    	]
	}
}

{
  "to": "{{Recipient-WA-ID}}",
  "type": "contacts",
  "recipient_type": "individual",
  "contacts": [
  	{
  		"addresses": [
  			{
  				"city": "<Contact's City>",
  				"country": "<Contact's Country>",
  				"country_code": "<Contact's Country Code>",
  				"state": "<Contact's State>",
  				"street": "<Contact's Street>",
  				"type": "<Contact's Address Type>",
  				"zip": "<Contact's Zip Code>"
  			}
  		],
  		"birthday": "<Contact's Birthday>",
  		"emails": [
  			{
  				"email": "<Contact's Email>",
  				"type": "<Contact's Email Type>"
  			}
  		],
  		"ims": [],
  		"name": {
  			"first_name": "<Contact's First Name>",
  			"formatted_name": "<Contact's Formatted Name>",
  			"last_name": "<Contact's Last Name>"
  		},
  		"org": {
  			"company": "<Contact's Company>"
  		},
  		"phones": [
  			{
  				"phone": "<Contact's Phone Number>",
  				"type": "<Contact's Phone Number Type>"
  			}
  		],
  		"urls": []
  	}
  ]
}

{
	"to": "{{Recipient-WA-ID}}",
	"type": "sticker",
	"recipient_type": "individual",
	"sticker": {
		"provider": {
			"name": "<Provider Name from Media Provider API, optional"
		},
		"link": "<Sticker Image Link>"
	}
}

{
	"publisher": "<Your Publisher Name>",
	"name": "<Your Sticker Pack Name>",
	"ios_app_store_link": "<Valid iOS App Store Link>",
	"android_app_store_link": "<Valid Android App Store Link>"
}

